    submitAction({
      Licenses: [applicationData],
    }, false, {isStakeholder: true, bpa: false});
  }

  useEffect(() => {
    if (action) {
      setConfig(
        configBPAREGApproverApplication({
          t,
          action,
          approvers,
          selectedApprover,
          setSelectedApprover,
          selectFile,
          uploadedFile,
          setUploadedFile,
          businessService,
          error
        })
      );
    }
  }, [action, approvers, uploadedFile, error]);

  return action && config.form ? (
    <Modal
      headerBarMain={<Heading label={t(config.label.heading)} />}
