import React, { useCallback, useEffect, useState } from "react";
import {
  SearchForm,
  Table,
  Card,
  Loader,
} from "@egovernments/digit-ui-react-components";
import { useFormContext } from "react-hook-form";
import { useTranslation } from "react-i18next";


const SearchApplicationDesktopView = ({columns, SearchFormFieldsComponent, onSubmit, data, error, isLoading, Count}) => {
    const { handleSubmit, setValue, getValues } = useFormContext();

    if (getValues("offset") == undefined) setValue("offset", 0);
    if (getValues("limit") == undefined) setValue("limit", 10);

    const [currPage, setCurrPage] = useState(Number(getValues("offset")) / Number(getValues("limit")));
    const { t } = useTranslation();

    const fetchLastPage = () => {
      setValue("offset", Count && Math.ceil(Count / 10) * 10 - getValues("limit"));
      handleSubmit(onSubmit)();
    };
  
    const fetchFirstPage = () => {
      setValue("offset", 0);
      handleSubmit(onSubmit)();
    };
  
    const onSort = useCallback((args) => {
      if (args.length === 0) return;
      setValue("sortBy", args.id);
      setValue("sortOrder", args.desc ? "DESC" : "ASC");
    }, []);
  
    function onPageSizeChange(e) {
      setValue("limit", Number(e.target.value));
      handleSubmit(onSubmit)();
    }
  
    function nextPage() {
      setValue("offset", Number(getValues("offset")) + Number(getValues("limit")));
      handleSubmit(onSubmit)();
    }
    function previousPage() {
      setValue("offset", Number(getValues("offset")) - Number(getValues("limit")));
      handleSubmit(onSubmit)();
    }
  
    useEffect(() => {
      if(!(getValues("offset") == undefined || getValues("limit") == undefined))
      setCurrPage(Number(getValues("offset")) / Number(getValues("limit")));
    }, [getValues("offset"), getValues("limit")]);
  
    const TableComponent = () => {
        if(isLoading){
            return <Loader/>
        } else{
            return data?.[0]?.display ? <Card style={{ marginTop: 20 }}>
                    {t(data?.[0]?.display)
                    .split("\\n")
                    .map((text, index) => (
                        <p key={index} style={{ textAlign: "center" }}>
                        {text}
                        </p>
                    ))}
                </Card> : <Table
                    t={t}
                    data={data}
                    columns={columns}
                    getCellProps={(cellInfo) => {
                        return {
                        style: {
                            minWidth: cellInfo.column.Header === t("ES_INBOX_APPLICATION_NO") ? "240px" : "",
                            padding: "20px 18px",
                            fontSize: "16px",
                        },
                        };
                    }}
                    onPageSizeChange={onPageSizeChange}
                    currentPage={currPage}
                    onNextPage={nextPage}
                    onPrevPage={previousPage}
                    pageSizeLimit={Number(getValues("limit"))}
                    onSort={onSort}
                    totalRecords={Count}
                    disableSort={false}
                    onLastPage={fetchLastPage}
                    onFirstPage={fetchFirstPage}
                    sortParams={[{ id: getValues("sortBy"), desc: getValues("sortOrder") === "DESC" ? true : false }]}
                />
        }
         
    }

    return <React.Fragment>
    <SearchForm onSubmit={onSubmit} handleSubmit={handleSubmit}>
      <SearchFormFieldsComponent onSubmit={onSubmit} handleSubmit={handleSubmit} isMobileView={false}/>
    </SearchForm>
    <TableComponent/>
  </React.Fragment>
}

export default SearchApplicationDesktopView
import React, { Fragment, useCallback, useMemo, useEffect, useState, useReducer } from "react";
import { useForm, FormProvider } from "react-hook-form";
import SearchFormFieldsComponent from "./SearchFormFieldsComponent";
import useSearchApplicationTableConfig from "./useTableConfig";
import SearchApplicationMobileView from "./SearchAtom/mobile";
import SearchApplicationDesktopView from "./SearchAtom/desktop";

const SearchApplication = ({ tenantId, t, onSubmit, data, error, isLoading, Count }) => {
  const [showToast, setShowToast] = useState(null);

  const methods = useForm({
    defaultValues: {
      // applicationNo: "",
      // mobileNumber: "",
      // fromDate: "",
      // toDate: "",
      // status: "",
      // offset: 0,
      // limit: 10,
      // sortBy: "commencementDate",
      // sortOrder: "DESC",
      // applicationType: {
      //   code: "BUILDING_PLAN_SCRUTINY",
      //   i18nKey: "WF_BPA_BUILDING_PLAN_SCRUTINY",
      // },
      // serviceType: {
      //   applicationType: ["BUILDING_PLAN_SCRUTINY", "BUILDING_OC_PLAN_SCRUTINY"],
      //   code: "NEW_CONSTRUCTION",
      //   i18nKey: "BPA_SERVICETYPE_NEW_CONSTRUCTION",
      // },
    },
  });

  useEffect(() => {
    methods.register("offset", 0);
    methods.register("limit", 10);
    methods.register("sortBy", "commencementDate");
    methods.register("sortOrder", "DESC");
  }, [methods.register]);

  const columns = useSearchApplicationTableConfig();

  const isMobile = window.Digit.Utils.browser.isMobile();

  // const searchFormFieldsComponentProps = { t, previousPage };

  const getRedirectionLink = () => {
    let redirectBS = "noc/inbox/application-overview";
    return redirectBS;
  };
  const propsMobileInboxCards = useMemo(
    () =>
      data?.map((data) => ({
        [t("NOC_APP_NO_LABEL")]: data.applicationNo,
        [t("NOC_COMMON_TABLE_COL_APP_DATE_LABEL")]: Digit.DateUtils.ConvertEpochToDate(data.auditDetails?.createdTime) || "-",
        [t("NOC_APPLICANTS_NAME_LABEL")]: data?.additionalDetails?.applicantName || "-",
        [t("NOC_SOURCE_MODULE_LABEL")]: data.source ? t(`MODULE_${data.source}`) : "-",
        [t("NOC_SOURCE_MODULE_NUMBER")]: data?.sourceRefId || "-",
        [t("WF_INBOX_HEADER_CURRENT_OWNER")]: data?.additionalDetails?.currentOwner || "-",
        [t("NOC_STATUS_LABEL")]: data.applicationStatus ? t(`${data.applicationStatus}`) : "-",
      })),
    [data]
  );

  if (isMobile) return <FormProvider {...methods}><SearchApplicationMobileView {...{SearchFormFieldsComponent, propsMobileInboxCards, isLoading, data, getRedirectionLink, onSubmit}} /></FormProvider>

  return <FormProvider {...methods}><SearchApplicationDesktopView {...{columns, SearchFormFieldsComponent, onSubmit, data, error, isLoading, Count}} /></FormProvider>
};

export default SearchApplication;
import { Loader } from "@egovernments/digit-ui-react-components";
import React from "react";
import { useRouteMatch } from "react-router-dom";
import EmployeeApp from "./pages/employee";
import ApplicationOverview from "./pages/employee/ApplicationOverview";
import NOCCard from "./pages/employee/EmployeeCard";
import Inbox from "./pages/employee/Inbox";
import NOCSearchApplication from "./pages/employee/SearchApplication/Search";

const NOCModule = ({ stateCode, userType, tenants }) => {
  const moduleCode = "common-noc";
  const { path, url } = useRouteMatch();
  const language = Digit.StoreData.getCurrentLanguage();
  const { isLoading, data: store } = Digit.Services.useStore({ stateCode, moduleCode, language });

  Digit.SessionStorage.set("NOC_TENANTS", tenants);

  if (isLoading) {
    return <Loader />;
  }

  if (userType === "citizen") {
    return <div></div>;
  }

  return <EmployeeApp path={path} stateCode={stateCode} />;
};

const componentsToRegister = {
  NOCModule,
  NOCCard,
  NOCApplicationOverview: ApplicationOverview,
  NOCInbox: Inbox,
  NOCSearchApplication
};

export const initNOCComponents = () => {
  Object.entries(componentsToRegister).forEach(([key, value]) => {
    Digit.ComponentRegistryService.setComponent(key, value);
  });
};
